import pandas as pd
import pickle
import streamlit as st
from sklearn import preprocessing


# In[5]:





# In[8]:


filename = 'model.pkl'
loaded_model = pickle.load(open(filename, 'rb'))
df = pd.read_csv('classified_data.csv')
df.head()


# In[9]:


df.drop(columns = ['Unnamed: 0'], inplace = True)
df.head()


# In[10]:


st.title('Iris Flower Detection')
st.header('Give the lengths of following in cms:')
sl = st.number_input('_Sepal Length_:purple[colors]:cherry_blossom:') 
sw = st.number_input('_Sepal width_:purple[colors]:cherry_blossom:')
pl = st.number_input('_Petal Length_:purple[colors]:cherry_blossom:')
pw = st.number_input('_Petal Width_:purple[colors]:cherry_blossom:')
st.button('Detect')
data = [[sl, sw, pl, pw]]
detect_flr = ' '


# In[16]:


if(st.button('Detect')):
    detect_flr = loaded_model.predict(data)[0]
    if detect_flr == 0:
        st.write('Iris-setosa')
        st.image("data:image/jpeg;base64,",caption = 'Iris-setosa')
    if detect_flr == 1:
        st.write('Iris-versicolor')
        st.image("data:image/jpeg;base64,",caption = 'Iris-versicolor')
    if detect_flr == 2:
        st.write('Iris-virginica')
        st.image("data:image/jpeg;base64,", caption = 'Iris-virginica')
    st.snow()


